
public class A1H2 {
//Write a program to print the sum (addition), multiply, subtract, divide and remainder of two numbers
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=20;
		int num2=5;
		System.out.println("addition : "+(num1+num2)+"\n"+"subtraction : "+(num1-num2)+"\n"+"multiply : "
		+(num1*num2)+"\n"+"divide : "+(num1/num2)+"\n"+"remainder : "+(num1%num2));

	}

}
