
public class A1H4 {
//Write a program to print the area and perimeter of a rectangle
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int l=10;
		int b=20;
		int area=l*b;
		int perimeter=2*(l+b);
		System.out.println("area : "+area+"\n"+"perimeter : "+perimeter);

	}

}
