
public class A1H6 {
//Write a program to convert Celsius into Fahrenheit and viceversa. (Variations) F = 32 +(9/5*C)

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double cel=18; //Fahrenheit:64.4
		double far=0;
		
		far=32+(9.0/5.0)*cel;
		System.out.println(far);
		

	}

}
