
public class A1H7 {
//Write a program to display total Marks and average of 6 subjects. (Variations)
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int mar=90, hin=92, eng=86, hist=99, geo=99, sci=95, sub=5;
		double total=mar+hin+eng+hist+geo+sci;
		double avg=total/sub;
		System.out.println("Total marks : "+total+"\n"+"Average of subjects : "+avg);
	}

}
