
public class A1H9 {
//Print a message on two lines using only one sys out statement Output: Hef "new line" Shine
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//character used with backspace(\) is an escape sequeces..
		
		
		System.out.println("Hef\nShine");  //new line
		System.out.println("Hef\tShine");	//tab spacing
		System.out.println("Hef\\Shine"); 	//insert backspace
		System.out.println("Hef\'Shine"); 	//insert single quotes
		System.out.println("Hef\"Shine");	//insert double quotes
		System.out.println("Hef\rShine"); 	//carriage return--it moves cursor to the beginning of the line
		
		
		
		System.out.println("Hef\\nShine");  //type \n
	}

}
